INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.sozialministerium.at/siteEN/Labour_Disabilities/Occupational_Safety_and_Health/Labour_Inspectorate/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria\">OSHwiki</a>.");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The general Labour Inspectorate (Arbeitsinspektion) is by far the largest of those bodies that have been installed to monitor safety and health of the employees. It covers industries, services, the public sector, and transport - following the July 2012 merger with Inspectorate of Transport. It is centralised and acts nationwide through 16 regional departments and one central office. Newly recruited inspectors follow a two-year training programme, with the emphasis on legislation and general OSH principles. At level of the Federal States, nine regional Labour Inspectorates for Agriculture and Forestry are installed.");
