INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.emploi.belgique.be\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Occupational safety and health (OSH) is a matter within the competence of the Federal Public Service Employment, Labour and Social Dialogue. The main executive agencies are the Directorate General for the Humanization of Work (DG Humanisation du travail – DGHT) and the Directorate General for the Control of the well-being at work (DG Contrôle du bien-être au travail). The Federal Public Service is the responsible administrative body led by the Federal Minister for Work.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
