INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "DG Humanization of work");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.emploi.belgique.be/defaultTab.aspx?id=332\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Directorate General for the Control of the Well-being at work is organized in three Divisions: (1) a Division for the preparation of the legislation, (2) a Division for the promotion of the well-being at work and (3) a Division for social consultation.</p><p>&nbsp;</p><p>The Division for the preparation of the legislation draws up the norms regarding well-being on the shop floor and interprets and evaluates these norms. It also deals with preparing these norms on a European and international level and the implementing of the European and international norms into Belgian law.</p><p>&nbsp;</p><p>The Division of promoting well-being at work arranges for promotion and information regarding well-being at work on an international and Belgian level. It develops action programmes on the distribution and use of “good practices” and networks for exchanging information on well-being at work.</p><p>&nbsp;</p><p>There are provincial committees for promoting work in each province with a view to reaching small businesses more effectively. They are tripartite committees comprising representatives of employers, employees and the administration of which the governors of the provinces are the president. They carry out regional information campaigns controlled by the DG Humanization of Work.</p><p>&nbsp;</p><p>The Division for social consultation on well-being at work organizes social consultation among other things, on the level of norms regarding well-being at work. It also manages the procedures for recognition and approval of the external actors involved in well-being at work.</p>");
