INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.mlsp.government.bg/index.php?lang=_eng\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The MLSP is part of the Bulgarian Council of Ministers (the executive state body).MLSP is in charge with developing, coordinating and implementing the policy on labour, professional qualification, income and living standard, industrial relations, health and safety at work, social security and social assistance.</p><p>According to the Health and Safety at Work Law the tasks of MLSP in the field of health and safety at work are:</p><ol><li>jointly with the Ministry of Health to analyze the status, trends and problems in providing healthy and safe working conditions and to propose measures for its improvement;</li><li>alone or with other ministries issues regulations on the provision of health and safety, organizes and coordinates the development of legislation in this area and establishes rules to ensure safe and healthy working conditions;</li><li>carries out an integrated control trough the Executive Agency \"General Labour Inspectorate\" of legislative compliance and fulfillment of obligations to ensure healthy and safe working conditions in all sectors and activities regardless of the form of ownership;</li></ol><p>establishes the terms and requirements for training, measurements and consulting in the field of safety at work;</p>");
