INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.mpsv.cz/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "In the Czech Republic occupational safety and occupational health has traditionally two distinct features. While the Ministry of Labour and Social Affairs (Ministerstvo práce a sociálních věcí) is responsible for occupational safety and labour inspection, the Ministry of Health (Ministerstvo zdravotnictví) is competent for public health that includes occupational health and is in charge of occupational health inspection. Although this distinction is also followed by the executive agencies of the two ministries, they co-operate closely on international, national and regional levels in the field of OSH. The systems of administrative authorities are run from state budget. Both are authorized to impose sanctions if they find severe deviations during inspection activities.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
