INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Occupational Safety Research Institute (VÚBP) is a public research institution founded by the Ministry of Labour and Social Affairs of the Czech Republic. One of the key activities of VÚBP is the application of methods and means in the area of risk prevention relating to health and safety at work. The institute also focuses on education and promotion of OSH, and is a consultation centre for OSH expertise.");
