INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "In Estonia, there are no prevention institutes as unlike the situation in some European countries. However, preventive activities are included and implemented, in the work of the Ministry of Social Affairs and the Labour Inspectorate most commonly through awareness raising campaigns.");
