INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://ec.europa.eu/social/main.jsp?catId=148&intPageId=685\">website</a> and in <a target=\"_blank\" href=\"https://www.eurofound.europa.eu/country/eu-level\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Senior Labour Inspectors’ Committee (SLIC) is one of the main institutions and mechanisms that monitor the enforcement of employees’ rights. SLIC started to meet in an informal way in 1982 to assist the European Commission in monitoring the enforcement of EU legislation at national level. A Commission decision (95/319/EC) gave the committee formal status in 1995 with a mandate to give its opinion to the Commission, either at the Commission’s request or on its own initiative, on all problems relating to Member States’ enforcement of EU law on health and safety at work.");
