INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.tvk.fi/en/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "In Finland, occupational accident insurance is offered by 12 private accident insurance companies under the supervision of the Ministry of Social Affairs and Health. The accident insurance system is financed through premiums paid by the employers. Worker´ Compensation center (Tapaturmavakuutuskeskus, TVK) coordinates the statutory accident insurance. The TVK is a liaison organisation for the insurance companies. It combines statistics on occupational accidents and diseases together with their causes and consequences, and helps prevent these in the future. An occupational safety and health committee, with representatives from the labour market organisations and the insurance sector, directs TVK’s OSH activities and co-ordinates the insurance sector. In addition, TVK investigates fatal occupational injuries. These are examined by an investigation board which has representatives from the central labour market organisations, insurance companies and the Centre for Occupational Safety (Työturvallisuuskeskus, TTK; Arbetarskyddscentralen)");
