INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.santepubliquefrance.fr\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France\">OSHwiki</a");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "S<p>Santé Publique France was created in April 2016 as the national public health agency, resulting from the merging of the French Institute for Public Health Surveillance (InVS), the French institute for Health Promotion and health Education (Inpes), and the Establishment for Public Health Emergency preparedness and response (Eprus). Santé Publique France serves the population in all aspects of public health based on scientific knowledge, data, and information, including on OSH issues. It supports the government and society in improving the health and well-being of the population, with the objective of reducing social health inequities in all areas of public health : infectious diseases, non infectious diseases, environmental health and occupational health. It contributes to better knowledge of work related diseases, plays an important role in health monitoring on OSH and provide important guidance in order to establish the table of occupational diseases. Santé Publique France analyzes up-to-date knowledge and data on the determinants of health and risk factors, provides decision makers with independant evidence-based guidance and proposes measures to protect the population from health threats, develops evidence-based interventions for prevention and health promotion.</p><p>&nbsp;</p><p>The agency, under the authority of Ministry of Health, is present throughout the national territory with regional units. Operating as a network, cooperation with partners and health professionals, including occupational physicians, enables to combine strengths.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 1, 0, @maxId+1, @maxId+2, @maxId+3);
