INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "French Standards Association (Association Française de Normalisation - AFNOR)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.afnor.org\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France\">OSHwiki</a");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>Standardisation and promotion of standardisation are handled by AFNOR and its delegated bodies and approved by the Minister for Industry. AFNOR is under the supervision of the Ministry for Industry. It represents France at the CEN (European Committee for Standardisation) and at the ISO (International Organization for Standardization).</p><p>&nbsp;</p><p>The technical work on standards is conducted through major standardization programs. Each program is steered by a Strategy Committee (CoS, Comité Stratégique), and the Occupational Safety and Health Strategy Committee (CoS Santé et Sécurité au Travail) covers OSH topics.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Federal Ministry of Labour and Social Affairs");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.bmas.de/EN/Our-Topics/our-topics.html\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Federal Ministry of Labour and Social Affairs (Bundesministerium für Arbeit und Soziales, BMAS)is the ministry in the areas of labour market, labour law, occupational safety and health, initial and continuing training, social security, pensions and the participation of persons with disabilities.</p><p>The BMAS prepares laws, ordinances and technical rules in the field of occupational safety and health. Furthermore, the Ministry supervises the social accident insurance institutions and the Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA) and it is represented in the National Occupational Safety and Health Conference of the Joint German OSH Strategyas well as in the National Prevention Conference.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.baua.de/EN/Home/Home_node.html\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA) is a public-law institution without legal capacity. As a federal authority (Bundesbehörde), it is directly responsible to the Federal Ministry of Labour and Social Affairs (BMAS). As a governmental research institution (Ressortforschungseinrichtung), it advises the Federal Ministry of Labour and Social Affairs in all matters of occupational safety and health and on the humane design of working conditions. As a federal institution with R&D functions, BAuA operates at the interface between science and politics and renders transfer services from the science system into policy, corporate practice and the broader society and vice versa. BAuA's tasks range from policy advice, research and development in OSH and the performance of sovereign duties through to transferring knowledge into corporate practice and the educational and instructional work done by the DASA Working World Exhibition.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
