INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "German Social Accident Insurance (DGUV)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.dguv.de/en/index.jsp\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The German Social Accident Insurance (Deutsche Gesetzliche Unfallversicherung, DGUV) is the umbrella organisation of the social accident insurance institutions for the industrial and the public sector[90]. The DGUV members consist of nine branch-specific social accident insurance institutions for the industrial sector (called Berufsgenossenschaften) and 19 social accident insurance institutions for the public sector (called Unfallkassen or Gemeindeunfallversicherungsverbände), four social accident insurance institutions for fire services (Feuerwehr-Unfallkassen) and the accident insurance of the Federal Government and the Railway Services (Unfallversicherung Bund und Bahn).");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
