INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Social Insurance for Agriculture, Forestry and Horticulture (SVLFG)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.svlfg.de/11-wir/index.html\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Social Insurance for Agriculture, Forestry and Horticulture (SVLFG) covers the social protection for accidents, health, pensions and nursing care of persons working in agriculture and forestry.</p><p>Since January 2013 the Social Insurance for Agriculture, Forestry and Horticulture is the succeeding umbrella organisation on federal level of the former Agricultural Social Insurance (Spitzenverband der landwirtschaftlichen Sozialversicherung, LSV) of the agricultural accident insurances (landwirtschaftlichen Berufsgenossenschaften), the agricultural pension funds (landwirtschaftlichen Alterskassen), the agricultural health insurances (landwirtschaftlichen Krankenkassen) and the agricultural long term care insurances (landwirtschaftlichen Pflegekassen).</p><p>The SVLFG attends to the interests of its members and promotes their tasks for the benefit of the insured and the companies. It represents the agricultural social security face to face with politics, federal, European and other national and international institutions, the social partners, federal courts and the European Court.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
