INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The German Institute for Standardisation (Deutsches Institut für Normung e.V., DIN)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.din.de/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The German Institute for Standardisation is the official German standardisation body. This registered non-profit association was acknowledged by an agreement with the German Federal Government. DIN represents German interests in European and international standards organisations. The primary task is to work closely together with its stakeholders (industry, government, associations) to develop consensus-based national and international standards that meet market requirements.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Directorate for Safety & Health at Work of the Ministry of Labour, Social Security & Social Solidarity");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ypakp.gr\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The&nbsp;Directorate for Safety &amp; Health at Work which promotes all issues related to occupational safety and health (except of the inspection and the enforcement of the national OSH legislation), is the principal OSH administration authority in the Ministry of Labour, Social Security &amp; Social Solidarity. The Directorate is responsible among other duties for the planning, organization, implementation, monitoring and evaluation of the national OSH strategy and policies, the national OSH System as well as the National OSH Programme, in accordance to the national priorities and the running EU OSH strategy. Its activities are mainly directed in the fields of OSH legislation, policy making, decision making, administration, information management and documentation, awareness raising, networking, education, training and research.&nbsp;</p><p>To bring out these actions the aforementioned Directorate, with principle operational objective to plan, monitor and evaluate actions under the National Strategy for Safety and Health at Work, is divided into (6) six departments:</p><ul type=\"square\"><li>Department of Working Conditions.</li><li>Department of Health Promotion, Harmful factors, Safety and Ergonomics.</li><li>Department of the National Focal Point for OSH Information and Documentation.</li><li>Department for the Major Accidents (under the Seveso Directive).</li><li>Department for OSH Training and Monitoring of Policies on OSH issues.</li><li>Department for People with Disabilities.</li></ul>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Labour Inspectorate (SEPE)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ypakp.gr/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Law 2639/1998 [21] instituted the Labour Inspectorate (SEPE) [22] in its current form. Its Central Services are located in Athens and a number of Regional Services are located throughout the country. The Regional Services are divided into two categories: Sixteen (16) Regional Directorates for the Inspection of Social Matters such as illegal occupation, lack of social security cover etc. and five (5) Regional Directorates for the Inspection of Safety and Health at Work (former Centres for the Prevention of Occupational Risk-KEPEK) which involve inspection of work conditions for safety, health and hygiene matters. The Labour Inspectorate is responsible for the inspection and application of the national legislation either on social matters (work times, wages, social security, occupational legality) or on the physical conditions of work (safety and health) and plays also an important consulting role for properly informing and guiding the employers in order to achieve a better compliance level. Law 3996/2011 [23] has introduced reforms to the Inspectorate.
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Social Insurance Institute (IKA)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ika.gr/en/home.cfm\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Social Insurance Institute (IKA) is by far the largest social security organization in Greece. IKA covers salaried employees in Greece or abroad for an employer who is based in Greece, as well as those employees who offer full-time or part-time personal labour on commissioned work agreements and who are not insured with any other main insurance agency.</p><p>In Greece the old Royal Decree 473/1961 concerning \"Contributions for Occupational Risk” is still in force. This Decree obliges nearly all employers to pay an insurance contribution to IKA amounting to 1% of the total wages. Theoretically this contribution is paid for protection against occupational risks. However, in reality the sum is not used for this purpose and is redirected to further finance IKA’s obligations (pensions etc). Given that occupational diseases are significantly underreported, this leads to reduced insurance coverage. A continuous demand by the Trade Unions is the establishment of a Body of Insurance against Occupational Risks under IKA’s umbrella.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The single State Social Security Organization (EFKA)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.efka.gov.gr/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Since the 1st of  January 2017 a single State Social Security Organization named EFKA has been put in operation as the result of the unification of the pre-existing social security institutions operating in Greece. Regulated by the law n.4387 (FEK 85/A’/12-5-2016) and with a view to ensuring a decent living and social protection, in terms of equality, social justice, redistribution and solidarity of generations, all state social benefits are granted within the framework of the umbrella organization EFKA which includes the National Health System for health benefits, the National Social Solidarity System for the welfare benefits and the National Social Security Funds for the  insurance benefits. Everyone is now insured with EFKA, employers and employees either as wage earners or self-employed pay their contributions to EFKA and all pensioners receive their main pension from the same organization.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Hellenic Organization for Standardization (ELOT)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.elot.gr/default_en.aspx\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Hellenic Organization for Standardization (ELOT) is the national body responsible for the elaboration, approval, publication and distribution of Hellenic Standards. The elaboration of Standards is entrusted to ELOT’s Technical Committees and Working Groups. Representatives from both the public and the private sector express their opinions on the different issues in order to achieve maximum possible consensus among them. The Technical Committee 59 (Health and Safety at Work) is responsible for the elaboration of standards on OSH issues. The Ministry of Labour, the Ministry of Development, the Technical Chamber of Greece (TEE), the Greek Chemists Association (EEX), the Greek Society for Environmental and Occupational Medicine (EEIEP), the General Confederation of Greek Workers (GSEE), the Federation of Greek Industries (SEV) as well as representatives from the private sector provide their expert opinion to this Technical Committee.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
