INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.piab.ie\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Under the Personal Injuries Assessment Board Act 2003, anyone intending to seek compensation for a personal injury (other than a personal injury arising out of medical negligence) must make an application through InjuriesBoard.ie. They are the statutory body which provides independent assessment of personal injury compensation for victims of accidents, including workplace accidents. Injuries Board.ie is staffed with public servants with relevant legal, insurance and medical expertise to assess OSH compensation claims, with an established independent panel of medical experts throughout Ireland. Injuries and ill-health from work related accidents and illnesses are claimed for by employees using employer’s public liability insurance through the courts for civil compensation. The state also has a system of Occupational injury and illness benefits paid to those out of work. These are paid from the budget of the Dept of Social and Family Protection (DSFP).");
