INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.iosh.co.uk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "IOSH was founded in 1945 and is the Chartered body for health and safety professionals. With more than 40,000 individual members, they are the biggest professional health and safety organisation in the world. The Ireland branch of IOSH was founded in 1982. Currently in the Ireland Branch there are five districts and five specialist sections. The Ireland Branch committee consists of the Chairperson, Vice-Chairperson, Treasurer, Secretary, Development Officer, some non-officer members, a representative from each of the districts and specialist sections.");
