INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"http://www.ohnai.ie/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>OHNAI is the national body for Occupational Health Nurses who work either as individual practitioners or team members in a workplace setting. The association supports it's members in their role of:</p><ul><li>Preventing accidents and ill health at work.</li><li>Providing care to employees through health promotion, health surveillance, rehabilitation and management of health and safety.</li></ul><p>Working to ensure professionalism, quality and legal compliance in occupational health practice.</p>");
