INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"_blank\" href=\"https://www.nsai.ie\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "NSAI (National Standards Authority of Ireland) is Ireland’s official standards body. They operate under the National Standards Authority of Ireland Act (1996) and are accountable to the Minister of Jobs, Enterprise and Innovation. NSAI are the national certification authority for CE Marking and provide a certification service to enable business demonstrate that Irish goods and services conform to applicable standards. As Ireland’s Official standards body, NSAI aims to inspire consumer confidence and create the infrastructure for products and services to be recognized and relied on, all over the world.");
