INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Luxembourg Institute for the Standardisation, Accreditation, Safety and Quality of Products and Services (Institut Luxembourgeois de la Normalisation et de l’Accréditation, de la Sécurité et Qualité des Produits et Services, ILNAS) is an administrative body under the supervision of the Minister responsible for the economy. It was created on the basis of the law of 20 May 2008 and commenced its activities on 1 June 2008.</p><p>&nbsp;</p><p>ILNAS fulfils the following roles among others:</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Standardisation,</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; The accreditation of conformity assessment bodies,</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; The monitoring of general product safety,</p><p>The promotion of quality management.</p>");
