INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The goal of the Luxembourg Association of Safety and Health Coordinators () is to:</p><ul><li>bring together safety and health coordinators in an association;</li><li>define the profession’s ethical and professional rules;</li><li>form study groups of relevance to the profession;</li><li>represent and defend the interests of the profession in the Grand Duchy of Luxembourg and abroad;</li><li>collaborate with or join any companies, associations or institutions in Luxembourg or abroad with similar, complementary or related goals;</li><li>offer its good offices to settle disputes and other controversial issues that are submitted to it;</li></ul><p>perform any activities directly or indirectly related to its goal and tending to further its attainment.</p>");
