INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Standards Organization of the Netherlands (Nederlands Normalisatie Instituut, NEN) supports the making of agreements on products, processes and services, and publishes standards. Employers and employees are mutually responsible for setting their own standards, so the NEN supplies them with the knowledge and experience they need for their consultations. The NEN works in close collaboration with European (CEN, CENELEC and ETSI) and worldwide (ISO, IEC and ITU) partners.");
