INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://sbca.nl/over-sbca/\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "There are many different Arbodiensten in the Netherlands, ranging from large national companies, to small regional or branch specific services. Arbodiensten usually are multidisciplinary and provide a wide range of services from preventive work checks, to checkups during sickness absenteeism, and health checks. A list of all certified arbodiensten can be found at the website of SBCA, which is the owner of the certification scheme of the arbodiensten. Arbodiensten can employ workers of various backgrounds, but are required to have expertise in at least four core disciplines: an occupational physician; an Industrial Hygienist, a Safety expert and a Work Organization expert.");
