INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Social Insurance Institution (ZUS)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.zus.pl/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>Social Insurance Institution (Zakład Ubezpieczeń Społecznych , ZUS) collects social and health insurance contributions of citizens and distributes benefits (e.g. pensions, sickness allowance, maternity allowance, etc.). The Social Insurance Institution is the disposer of the Social Insurance Fund (Fundusz Ubezpieczeń Społecznych, FUS; SIF) established on 1 January 1999 by virtue of the Social Insurance System Act, to perform social insurance related tasks[34]. The following funds are distinguished within SIF:</p><ul><li>old-age pension fund, established to finance old-age pensions and public deficit&nbsp; expenditure in order to guarantee the pay out of funded pensions;</li><li>pension fund established to finance, among others: incapacity benefit, training benefit, survivors’ pensions, supplements to survivors’ pensions for complete orphans, nursing allowance, funeral grants, etc.;</li><li>sickness fund, established to finance sickness, maternity, care, compensatory allowances, rehabilitation benefits;</li><li>accident fund, established to finance occupational accident pensions and allowances, lump-sum compensations, incapacity benefit as a direct result of occupational accident or disease.</li></ul><p>Compulsory occupational accident insurance covers persons subject to pension insurance, for example: employees, freelancers (contractors), members of agricultural production cooperatives, persons running an agriculture related business and their contractors.</p><p>A percentage rate of the occupational accident insurance contribution is differentiated for individual contribution payers and depends on the occupational risk category as well as the number of persons covered by the work accident insurance.</p>");
