INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "State Mining Authority (Wyższy Urząd Górniczy, WUG)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.wug.gov.pl/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>State Mining Authority (Wyższy Urząd Górniczy, WUG) was established by the Geological and Mining Law Act and is presided by the President of the State Mining Authority who is subordinate to the Minister of Energy (Minister Energii). The main responsibility of the State Mining Authority is to perform control and supervision over work safety and health; fire protection; mine rescue; management of mineral deposits in the extraction process; environmental protection, including damage prevention; mining plant construction and closure, including land reclamation and post mining area rehabilitation.</p><p>The mission of the State Mining Authority is to:</p><ul><li>improve work safety in mines;</li><li>protect miners’ health;</li><li>ensure efficient and sustainable management of mineral deposits;</li></ul><p>reduce the negative impact of the extractive industry on the environment</p>");
