INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "State Sanitary Inspectorate Inspection (Państwowa Inspekcja Sanitarna, PIS)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://gis.gov.pl/en/ \">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>State Sanitary Inspectorate Inspection (Państwowa Inspekcja Sanitarna, PIS) is subordinate to the Ministry of Health and presided by the Chief Sanitary Inspector. The State Sanitary Inspectorate is the primary institution responsible for the public health protection. It focuses particularly on infectious disease control, food and nutrition safety, environmental hygiene, health promotion and other public health related issues. The State Sanitary Inspectorate realises its goals through the following steps:</p><ul><li>conducting a sustained and preventive sanitary supervision;</li><li>preparing epidemiological analysis, studies and assessments;</li><li>overseeing working conditions in various workplaces, focusing on harmful factors such as dust, noise, vibration, chemical agents and their levels;</li><li>supervision of environmental hygiene and food safety,</li></ul><p>promotion of public health, dissemination of proper hygiene habits and disease prevention methods.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
