INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Labour Inspectorate (Państwowa Inspekcja Pracy, PIP)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.pip.gov.pl/en\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>National Labour Inspectorate (Państwowa Inspekcja Pracy, PIP) is subordinate to the Lower Chamber of the Polish Parliament (Sejm) and supervised by the Labour Protection Council whose role it to evaluate the Inspectorate’ programmes, tasks and activities, and analyse labour protection issues at national level. The Inspectorate is formed of the Chief Labour Inspectorate and District Labour Inspectorates and presided by the Chief Labour Inspector. A District Labour Inspectorate covers one or more provinces. Sub-district offices may be established within the structure of District Labour Inspectorates.</p><p>The main responsibilities of the National Labour Inspectorate’s include:</p><ul><li>supervision and inspection of enterprise compliance with labour law;</li><li>OSH regulations compliance audit of refurbished and modernised workplaces, machinery, technical devices and other technologies;</li><li>legal employment and other paid work inspection (including employment of foreign workers);</li><li>OSH standards compliance audit of sold PPE merchandise as governed by separate regulations;</li><li>undertaking steps to reduce health related work-hazards;</li><li>cooperation with environmental protection agencies on auditing regulations aimed at countering industry environmental hazards;</li></ul><p>reviewing labour law acts drafts.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
