INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Ministry of Family, Labour and Social Policy");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.mpips.gov.pl/en/\">website</a> and in <a target=\"blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Ministry of Family, Labour and Social Policy occupies a key role in the labour protection system in Poland. The Ministry is the main body responsible for the development and implementation of OSH national strategy and policies.</p><p>The institutions overseeing enterprise compliance with the OSH regulations include the National Labour Inspectorate (Państwowa Inspekcja Pracy), the State Sanitary Inspection (Państwowa Inspekcja Sanitarna), the Office of Technical Inspection (Urząd Dozoru Technicznego), and the State Mining Authority (Wyższy Urząd Górniczy).</p>");
