INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Protection against Occupational Hazards Department (DPRP - Departamento de Protecção contra os Riscos Profissionais");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www2.seg-social.pt/left.asp?05.17\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Protection against Occupational Hazards Department (DPRP - Departamento de Protecção contra os Riscos Profissionais), is the service of the Institute of Social Security (Instituto de Segurança Social), nationwide responsible for managing the treatment and recovery from illness or disability arising from occupational hazards. According to Portaria) no. 135/2012 of 08 May&nbsp; the competences of DPRP at company level, are as follows:</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To evaluate and determine the disability of injury, functional disturbance or disease arising from occupational hazards;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To ensure the provision of medical care to treat disease or disability resulting from occupational hazards;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To pay compensation for temporary disability and permanent disability pensions;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To promote clinical recovery and professional reclassification of beneficiaries with an occupational disease;</p><p id=\"tmgmt-1\">To promote the placement of rehabilitated workers in occupations compatible with his physical condition and ability to work.</p><p id=\"tmgmt-2\">&nbsp;</p>");
