INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.dgs.pt/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Division of Environmental and Occupational Health (DSAO - Divisão de Saúde Ambiental e Ocupacional (DSAO) from Directorate General of Health (DGS – Direção Geral de Saúde)  has responsibilities to propose strategies and specific programs for evaluation coordination and collaboration in the management of risk to human health in several areas, notably water, the built spaces, waste, chemical and biological substances, genetically modified organisms and ionizing and non ionizing radiation; for monitoring, technical advice and licensing facilities, equipment and chemical and biological substances in accordance with law; to propose strategies, coordinate activities and ensure programs within the occupational health; to propose strategies and coordinate programs and activities in ensuring the prevention of accidents.");
