INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Directorate-General for Employment and Labour Relations (DGERT- Direcção-Geral do Emprego e das Relações de Trabalho) is responsible for helping to develop policies on employment and vocational training and on labour relations, including working conditions and health, safety and welfare at work. It is also responsible for monitoring and encouraging collective bargaining and the prevention of trade disputes, and for promoting the accreditation of training bodies.");
