INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Working Conditions Authority (ACT- Autoridade para as Condições de Trabalho)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\" http://www.act.gov.pt/(pt-PT)/Paginas/default.aspx\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Working Conditions Authority (ACT- Autoridade para as Condições de Trabalho) is responsible for promoting improved working conditions by ensuring compliance with labour regulations and working conditions in the context of the private sector and compliance with working conditions in the public sector, and for promoting occupational risk prevention policies. The central services of ACT are located in Lisbon, and decentralized services, spread over all districts of Portugal.</p><p>&nbsp;</p><p>The central services of ACT, are constituted by three service directorates and 10 divisions:</p><ul><li>Directorate of Support to the supervision (DSAAI);</li><li>Directorate for the Promotion of Safety and Health at Work (DSPSST);</li></ul><p>Services Directorate for the Management Support (DSAG).</p>");
