INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Romanian Standards Association (ASRO)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.asro.ro/ \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>ASRO represents the National Standardization Body that develops standardization activities in all the fields of the European and international standardization as member of ISO, IEC, CEN, CENELEC and ETSI.</p><p>ASRO is a non-profit private body of public interest authorized by the Government through the Law No. 355/2002. In Romania, ASRO is developing the standardization activity since 1928 when it was put in place for all the national economy sectors.</p><p>At present, ASRO operates through the representatives of all the economic sectors, the national authorities, the industry, R&amp;D entities, the academic media, employer and employee organizations’ members, consumers’ associations, etc.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
