INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The Labour Inspection (IM)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p><span lang=\"EN-US\">The Labour Inspection (IM)</span><span lang=\"EN-US\">– represents the competent public authority in charge with the unitary enforcement of the legislation on occupational safety and health within public and private undertakings of all the economic sectors.<span class=\"apple-converted-space\">&nbsp;</span></span></p><p><span lang=\"EN-US\">The Labour Inspection establishment and organizational structure are regulated by the Law No. 108/1999 republished and also by the Regulation of organization and functioning approved through the Government Decision No. 1377/2009 amended.<span class=\"apple-converted-space\">&nbsp;</span></span></p><p><span lang=\"EN-US\">The Labour Inspection main objective resides in controlling the observance of the legal obligations by the employers from the public and private sector both in terms of labour relations and the employees’ safety and health at work of all the economic sectors.<span class=\"apple-converted-space\">&nbsp;</span></span></p><p><span lang=\"EN-US\">The Labour Inspection includes a number of 41 Labour Territorial Inspectorates in its organizational structure. They correspond to the 41 districts of the country enforce the Labour Inspection policy at territorial level. The Labour Inspectorate of Bucharest constitutes a separate entity corresponding to the size of the capital of the country and its significant number of companies.</span></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
