INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Ministry of Health (MS)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.ms.gov.ro/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Ministry of Health (MS) is a legal person and central administration body in the field of public health care.</p><p>Similarly with the Ministry of Labour, Family, Social Protection and Elderly, the Ministry of Health transposes into practice the governmental policies for the healthcare sector, both within the public health care system and in private medical units.</p><p>In the occupational health area, the Ministry of Health is drafting regulations for the medical sector, particularly for the development of the health at work activities, in line with the EU legislation in force.</p><p>At the same time, the Ministry of Health promotes the education of the occupational medicine physicians at academic level in collaboration with the Ministry of National Education, through occupational medicine clinics hosted by the biggest hospitals in the country.</p><p>The Ministry of Health is also elaborating and implementing adequate strategies and programmes meant to the improvement of workers’ health at the workplace and the reduction/elimination of the risk factors that may result in severe occupational diseases with significant costs in terms of human life and financial resources.</p><p>The Ministry of Health also co-ordinates the activity of the Public Health Institute in charge with monitoring the public health at national level, including the workplace related diseases, and the evidence of the occupational diseases as well.</p>");
