INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.mmuncii.ro/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Ministry of Labour, Family, Social Protection and Elderly (MMFPSPV) is a specialized body of the central public administration and legal person subordinated to the Government of Romania, in charge with the implementation of the governmental policies in the area of labour, family, equal opportunities, social protection and the elderly.</p><p>MMFPSPV acts in the OSH field through the Direction of Safety and Health at Work co-ordinated at the Ministry top management level by a Secretary of State. This Direction coordinates two research and development (R&amp;D) national institutes in the field of OSH and social security, respectively, while also monitoring the implementation of the policies and legislative acts in the area.</p>");
