INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.sutn.sk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia \">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Slovak Standards Institute (Slovenský ústav technickej normalizácie – SÚTN) has been established by the Slovak Office of Standards, Metrology and Testing (Úrad pre normalizáciu, metrológiu a skúšobníctvo Slovenskej republiky – ÚNMS SR). SUTN is exclusively designated to development, approval and publishing of Slovak Standards, fulfilment of obligations following from international contracts and from the membership in international and European standards organisations.");
