INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.socpoist.sk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia \">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The statutory institution has a nationwide competency in the area of sickness insurance, pension insurance, accident insurance, unemployment insurance and guarantee insurance. The headquarters regulates, controls and methodologically guides the 37 branch offices. It is in charge of the pension insurance area and of two types of benefits from the accident insurance. The executive body branch offices are responsible for the collection and recovery of sickness, unemployment and guarantee contributions, benefits pay out, as well as for a direct contact with clients.");
