INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Public Health Authority of the Slovak Republic (Úrad verejného zdravotníctva Slovenskej republiky – UVZSR)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.uvzsr.sk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia \">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "State supervision in the field of public health is determined by the Act No. 355/2007 Coll. The Public Health Authority of the Slovak Republic, which is a budgetary organisation of the state with a registered office in Bratislava, is the supreme office for the regional public health authorities. It manages, controls and coordinates the execution of state administration carried out by regional public health offices. State administration in the sector of health on regional level is ensured by 36 regional public health authorities, 8 of which are in county towns, ensure professional and methodical directing of the others. Public health authorities supervise employers’ adherence to legislation and fulfilment of measures directed for prevention of occupational diseases. They also supervise the performance of occupational health services, and carry out specialized tasks focused on monitoring of health status of employees in relation with working conditions. The public health authorities provide counselling for employers on the implementation of pertinent legal requirements.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
