INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Occupational health and hygiene are within the competence of the Ministry of Health of the Slovak Republic. Therefore there are separate executive agencies of the ministries, which closely co-operate on national and regional levels in the field of OSH");
