INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Traditionally, occupational safety and occupational health are distinct features in Slovakia. The Ministry of Labour, Social Affairs and Family of the Slovak Republic[29] is responsible for occupational safety and labour inspection.");
