INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.slo-akreditacija.si/ \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Slovenian Accreditation (Slovenska akreditacija) is a public institute for carrying out tasks of national accreditation services. It follows the Accreditation Act and the Decision on the establishment of a public institute for Slovenian Accreditation (SA). It is responsible for the harmonious development of standards in Slovenia and is a member of the European cooperation for Accreditation, the International Accreditation Forum and the International Laboratory Accreditation Cooperation.");
