INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.zpiz.si \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Institute of Pension and Invalidity Insurance of Slovenia is also a public institution. It is a carrier and provider of compulsory pension and invalidity insurance. Compulsory insurance is covered by contributions of insured persons, employers and the Republic of Slovenia. The rates of contributions are determined by the Social Security Contributions Act . Compulsory pension and invalidity insured persons are: employees in Slovenia; Slovenian nationals employed in foreign companies; self-employed; farmers; apprentices; top sportsmen, top chess players and others. This compulsory insurance covers invalidity pension, occupational rehabilitation, invalidity benefit, reassignment, part-time work and other benefits.");
