INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.zzzs.si \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Health Insurance Institute of Slovenia is a public institution that carries out compulsory health insurance. Compulsory health insurance is covered by contributions of insured persons, employers, the Republic of Slovenia and others. The rates of contributions are determined by the Social Security Contributions Act. Categories of persons that are compulsorily health insured person are: employees in Slovenia; Slovenian nationals employed in foreign companies; self-employed; farmers; apprentices; top sportsmen, top chess players and others. Compulsory health insurance allows insured persons to claim the right to health services, medicines and medical devices and to cash compensation such as salary compensation during temporary absence from work, funeral and death and reimbursement of travel expenses in connection with the exercise of rights to health services.");
