INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Health Inspectorate of the Republic of Slovenia performs inspection over implementation of laws and other regulations with the objective of ensuring public health covering areas such as infectious diseases, food safety, drinking water, the medical profession, etc.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
