INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.mz.gov.si/en/ \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Ministry of Health of the Republic of Slovenia deals with activities concerning health at work that are carried out by occupational physicians. Within its scope, the control function concerning protection of human health against the harmful effects of ionizing and non-ionizing radiation is performed by the Slovenian Nuclear Safety Administration (Uprava Republike Slovenije za varstvo pred sevanji). Drafting of regulations, granting of concessions to occupational physicians, coordination of health at work programmes and leading and coordinating activities concerning health promotion for managers and working population are within the powers of the Ministry itself.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
