INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Labour Inspectorate");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.id.gov.si/en/ \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Labour Inspectorate The Slovenian Labour Inspectorate in its repressive role performs inspection duties in the field of labour relations, health and safety at work and since 2004 also in the field of social welfare. From 1 January 2005, it has acted as a minor offences authority on First Instance in an accelerated procedure on the basis of the Minor Offences Act  [the Labour Inspectorate supervises the implementation of the Health and Safety at Work Act, which also defines offenses for violation of its provisions. When labour inspectors carry out regular inspection duties upon this law, in the case of offenses detected, they at the same time keep the proceedings for the offense in an accelerated procedure, as a First Instance authority. First Instance authority means that against the labour inspector’s decision about the offense there may be an appeal in the form of a request for judicial protection. Through its preventive and advisory role as Inspectorate, it provides expert help and advice both to employers and employees, companies and workers, it is involved in law-making and it informs the public of its findings, actions and consequences of violations of regulations in order to protect the rights of legal and natural persons.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
