INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Trade Union Institute for Work, Environment and Health (Instituto Sindical de Trabajo, Ambiente y Salud, ISTAS) was founded in 1996 by the Spanish trade union Comisiones Obreras (CC.OO), which still supports its work. This non-profit, independent research and education institute was set up as a foundation. The work is supervised by the Board of Directors, which is responsible for strategic and financial planning. The majority of the members are nominated by the CC.OO trade union");
