INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://trauma.lbg.ac.at/%20\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong>The Austrian Workers' Compensation Board/Austrian Social Insurance for Occupational Risks (AUVA) Center for Traumatology Research encompasses the AUVA Institute for Traumatology Research and, as of 1980, the Ludwig Boltzmann Institute for Experimental and Clinical Traumatology (Ludwig Boltzmann Society), receiving additional support from the Trauma Care Consult since 1998. The Ludwig Boltzmann Institute for Experimental and Clinical Traumatology has been the core of the Austrian Cluster for Tissue Regeneration since 2006 which was further enlarged in 2013/2014<br /><br />See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
