INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.aamp.at\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong>The AAMP is the most important Austrian institution for the education and training of occupational physicians, occupational health professionals and other persons interested in occupational medicine or work-related prevention<br /><br />See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
