INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.emploi.belgique.be/moduleTab.aspx?id=556&amp;idM=127\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong>Belgium does not run a national OSH research institute. Research in the field of health and safety at work is mainly carried out by the research groups at the universities. The Belgian DG for humanization of work runs a directorate DIRACT- DIOVA (Direction de la recherche sur l’amélioration des conditions de travail), which is responsible for the coordination and promotion of research on the improvement of the working conditions.<br /><br />See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
